<?php/*|--------------------------------------------------------------------------| Web Routes|--------------------------------------------------------------------------|| Here is where you can register web routes for your application. These| routes are loaded by the RouteServiceProvider within a group which| contains the "web" middleware group. Now create something great!|*/Route::get('/', function () {    return view('welcome');});Auth::routes();Route::middleware(['auth','home'])    ->group(function () {        Route::get('/home/', 'HomeController@index')->name('home');    });Route::middleware(['auth', 'admin'])    ->prefix('admin')    ->name('admin.')    ->group(function () {        Route::get('/home/', 'AdminController@index')->name('home');        Route::get('/products/', 'AdminController@products')->name('products');        Route::get('/product/{id}', 'AdminController@product')->where('id', '[0-9]+')->name('product');        Route::any('/product/', 'AdminController@productEdit')->name('productEdit');        Route::get('/orders/', 'AdminController@orders')->name('orders');        Route::get('/categories/', 'AdminController@categories')->name('categories');        Route::get('/users/', 'AdminController@users')->name('users');    });